import{_ as s,r as o,o as n,c as i,b as e,d as a,e as r,a as l}from"./app-VdCEKWSB.js";const p={},d=l('<h1 id="standup" tabindex="-1"><a class="header-anchor" href="#standup" aria-hidden="true">#</a> Standup</h1><h2 id="overview" tabindex="-1"><a class="header-anchor" href="#overview" aria-hidden="true">#</a> Overview</h2><p>Stand up meeting is a daily meeting that is held to keep the team in sync. It is a great way to keep everyone on the same page and to identify blockers early on.</p><h2 id="format" tabindex="-1"><a class="header-anchor" href="#format" aria-hidden="true">#</a> Format</h2><ul><li><p><strong>Time:</strong> 11:45 PHT</p></li><li><p><strong>Duration:</strong> 15 minutes</p></li><li><p><strong>Location:</strong> To be decided</p></li><li><p><strong>Attendees:</strong> All team members</p></li></ul><h2 id="agenda" tabindex="-1"><a class="header-anchor" href="#agenda" aria-hidden="true">#</a> Agenda</h2><ul><li><p><strong>What did you do yesterday?</strong></p></li><li><p><strong>What are you going to do today?</strong></p></li><li><p><strong>What are the blockers?</strong></p></li></ul><h2 id="rules" tabindex="-1"><a class="header-anchor" href="#rules" aria-hidden="true">#</a> Rules</h2><ul><li><p><strong>Be on time.</strong></p></li><li><p><strong>Be present.</strong></p></li><li><p><strong>Be prepared.</strong></p></li><li><p><strong>Be honest.</strong></p></li><li><p><strong>Be respectful.</strong></p></li><li><p><strong>Be concise.</strong></p></li></ul><h2 id="tips" tabindex="-1"><a class="header-anchor" href="#tips" aria-hidden="true">#</a> Tips</h2><ul><li><p><strong>Keep it short.</strong> Stand up meeting should not take more than 15 minutes. If you need to discuss something in detail, schedule a separate meeting.</p></li><li><p><strong>Keep it simple.</strong> Stand up meeting is not a status report. It is not a place to discuss the details of your work. It is a place to share the progress and to identify blockers.</p></li><li><p><strong>Keep it focused.</strong> Stand up meeting is not a place to discuss the details of your work. It is a place to share the progress and to identify blockers.</p></li><li><p><strong>Keep it positive.</strong> Stand up meeting is not a place to complain about your work. It is a place to share the progress and to identify blockers.</p></li><li><p><strong>Keep it honest.</strong> Stand up meeting is not a place to lie about your work. It is a place to share the progress and to identify blockers.</p></li><li><p><strong>Keep it respectful.</strong> Stand up meeting is not a place to insult your team members. It is a place to share the progress and to identify blockers.</p></li><li><p><strong>Keep it concise.</strong> Stand up meeting is not a place to ramble about your work. It is a place to share the progress and to identify blockers.</p></li></ul><h2 id="resources" tabindex="-1"><a class="header-anchor" href="#resources" aria-hidden="true">#</a> Resources</h2>',12),c={href:"https://www.atlassian.com/agile/scrum/standups",target:"_blank",rel:"noopener noreferrer"},h={href:"https://www.scrum.org/forum/scrum-forum/32936/daily-standup-notes",target:"_blank",rel:"noopener noreferrer"};function u(g,m){const t=o("ExternalLinkIcon");return n(),i("div",null,[d,e("ul",null,[e("li",null,[e("a",c,[a("Atlassian Standup Meeting"),r(t)])]),e("li",null,[e("a",h,[a("Scrum Standup Meeting"),r(t)])])])])}const b=s(p,[["render",u],["__file","standup.html.vue"]]);export{b as default};
